var ProtoBuf = require("protobufjs");
var contants = require('./constants');

module.exports.loadBuilder = function() {
	if (contants.debug) {
		console.log("Loading Protocol Buffer Model from " + contants.protofile);
	}

	// create the builder from the proto file
	var builder = ProtoBuf.loadProtoFile(__dirname + "/" + contants.protofile);
	return builder.build(contants.kplConfig[contants.useKplVersion].messageName);
};
